        <div class="user-info" v-if="isLogin">
          <el-badge value="new" type="danger" style="margin-right: 20px;">
                <el-button size="mini">消息</el-button>
            </el-badge>
            <span>
            <img v-if="avatarUrl" class="avatar" :src="avatarUrl" alt />
            <img v-else class="avatar" src="../assets/avatar.jpg" alt />
          </span>
            <span class="user-name">
            欢迎:
            <span class="name-text">{{nickName}}</span>
            
            <span class="login-out" @click="loginOut">注销</span>
            </span>
        </div>
        <div class="user-info" v-else style="padding-top: 5px">
            <!-- <span >
                <img v-else class="avatar" src="../assets/avatar.jpg" alt />
          </span>-->
            <span class="user-name">
            <span class="login-out" @click="login">请登录</span>
            </span>
        </div>
    </div>
</template>

<script>
import { getUserInfo } from "api/index";
export default {
    data() {
        return {
            avatarUrl: "",
            nickName: "",
            isLogin: false
        };
    },
    created() {
        this.getUserInfo();
        if (!!localStorage.token) {
            this.isLogin = true;
        } else {
            this.isLogin = false;
        }
    },
    methods: {
        login() {
            this.$router.push("/login");
        },
        getUserInfo() {
            getUserInfo()
                .then(res => {
                    let data = res.data;
                    if (res.data.code === 0) {
                        this.avatarUrl = data.content.avatar;
                        this.nickName = data.content.nick_name;
                    } else {
                        // this.$message({
                        //     type: "error",
                        //     message: data.message,
                        // })
                        // this.$router.push("/login");
                    }
                })
                .catch(err => {});
        },
        loginOut() {
            localStorage.removeItem("token");
            this.$router.push("/login");
            this.$message({
                type: "success",
                message: "退出成功"
            });
        }
    }
};
</script>

